INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, '一位WordPress评论者', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2017-03-13 21:57:02', '2017-03-13 13:57:02', '嗨，这是一条评论。\n要开始审核、编辑及删除评论，请访问仪表盘的“评论”页面。\n评论者头像来自<a href=\"https://gravatar.com\">Gravatar</a>。', 0, '1', '', '', 0, 0);
INSERT INTO `wp_links` (`link_id`, `link_url`, `link_name`, `link_image`, `link_target`, `link_description`, `link_visible`, `link_owner`, `link_rating`, `link_updated`, `link_rel`, `link_notes`, `link_rss`) VALUES
(1, 'http://www.w3school.com.cn/', 'W3C官网', '', '_blank', 'w3c的官网，内容比较全，有各种语言的入门教程', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(2, 'http://www.runoob.com', '菜鸟教程', '', '_blank', '感觉和w3c做的好像，不过好像界面会更优美一点，不过我没有看过上面的教程', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(3, 'http://www.php100.com/', 'php100', '', '_blank', 'php中文网-互联网开发者平台', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(4, 'http://code.ciaoca.com', '前端开发仓库', '', '_blank', '前端总结网站，有一些手册和在线编辑功能，同时有一些比较实用的插件地址', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(5, 'http://www.csszengarden.com', 'CSS禅意花园', '', '_blank', '从视觉上形象地展示了基于CSS设计的网站所能达到的效果', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(6, 'http://www.imooc.com/', '慕课网', '', '_blank', '更加有针对性地解决一些问题，而且还可以下载源码，是首推的视频学习网站', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(7, 'http://www.jikexueyuan.com/', '极客学院', '', '_blank', '不过对于小白来说更加亲切，学习进阶路线更加清晰，但是大部分需要会员，北邮人bt上有会员资源。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(8, 'http://www.miaov.com/', '秒味课堂', '', '_blank', '网站设计的很精美,各种CSS和JS动画让你不会厌倦,就算不看视频看看他的网站设计思路也会获益匪浅.', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(9, 'http://www.ruanyifeng.com/home.html', '阮一峰', '', '_blank', '阮一峰，经济学博士，但是也是IT研究人员，主要专注于网站制作，他阅读量非常大，而且涉猎面比较广，相信这个网站教给你的不会局限于技术。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(10, 'http://www.liaoxuefeng.com/', '廖雪峰', '', '_blank', '廖雪峰，全栈工程师，python专家，关于前端的教程没有什么特色，可以作为补漏的教材吧。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(11, 'http://www.yanshiba.com/', '燕十八博客', '', '', '燕十八,PHP培训师,但是涉猎比较广,而且个人感觉讲的比较明白清楚,博客上的内容虽然比较少,但是肯定会有所帮助.', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(12, 'http://www.css88.com/', 'Web前端开发', '', '', '专注前端开发,关注用户体验,内容很丰富,不仅有前端相关的新闻,还有一些教程资源,同时还有各种中文文档,是目前为止翻译的最好的中文文档,最重要的是,这竟然是某个前端开发工程师的个人网站!', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(13, 'http://strml.net/', '大牛的自我介绍', '', '', '初看这个网站不会很吸引人，但它却是一个创意与技术的完美结合作品，一位前端程序员用很有趣的方式介绍了自己，同时也展现他娴熟的技术，不得不赞一下！', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(14, 'http://www.easyicon.net/', 'easyicon', '', '_blank', '是一个免费搜索图标的搜索引擎站点，国内非常不错的搜索图标的站点之一，收录了超过40万个精美PNG、ICO、YCNS图标，内容非常丰富，提供中文、英文、繁体中文语言界面。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(15, 'http://www.iconpng.com/', 'iconpng', '', '_blank', '是一个中文的在线图标搜索引擎，不仅可以搜索到大量的 PNG 格式图标，还有图标系列的集合可以下载，另外 IconPng 还支持图标的 tag 和图标分类，可以方便的找到自己想要找的大量类似图标', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(16, 'http://tongji.baidu.com/', '百度统计', '', '_blank', '包括流量相关的网站统计、推广统计、移动统计三部分内容。分析内容包括趋势分析、来源分析、页面分析、访客分析、定制分析和优化分析。其中的页面点击热力图功能不错。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(17, 'http://www.google.com/analytics/', '谷歌分析', '', '_blank', '包括流量分析工具、内容分析、社交分析、移动分析、转化分析、广告分析几部分内容', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(18, 'http://www.crazyegg.com/', 'Crazy egg热力图', '', '_blank', '主要特点是对页面热点追踪分析的热力图，功能不错', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(19, 'https://cube.datastory.com.cn/', '数说立方', '', '_blank', '本质上是一个BI产品，配备百亿级社交数据库，支持全网公开数据实时抓取，并搭载了分布式搜索、语义分析、数据可视化三大引擎系统的海量计算平台，实现数据处理“探索式分析”和“秒级响应”的两个核心功能。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(20, 'http://sc.chinaz.com/yinxiao/', '站长素材', '', '_blank', '', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(21, 'http://www.yinxiao.com', '音效网', '', '_blank', '', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(22, 'http://www.2gei.com', '爱给网', '', '_blank', '', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(23, 'http://www.shengyin.com', '声音网', '', '_blank', '', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(24, 'https://offers.adobe.com/en/na/audition/offers/audition_dlc.html', 'ADOBE', '', '_blank', '这是ADOBE官方提供的免费音源素材，音效素材总体量在20G左右，提供免费下载和使用，是非常好的内容了，可千万别错过！', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(25, 'http://www.freesfx.co.uk', 'FREESFX', '', '_blank', '这支网站是朋友推荐的，还并未使用过，据说音源质量较高，该网站提供免费素材。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(26, 'http://freesound.org', 'freesound', '', '_blank', 'FreeSound实际是一个社区，该网站是一个协作型的团队合作式音频发布站点，世界各地的用户可以在该网站分享和上传音效内容，你可以在该网站找到非常多的音效，而且很多音效是可以直接在项目中使用的。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(27, 'http://www.sound-effects-library.com', 'sound-effects-library', '', '_blank', '同行推荐的专业门户，可自行研究', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(28, 'https://100audio.com', '100AUDIO', '', '_blank', '100audio是一支体验和操作都非常优质的音效素材网站，但是因为定位是商业网站，所以说，网站上的所有音源都是需要付费获得的，但是这里的音源基本都是正版，可以让你免去版权纷争。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(29, 'https://www.pond5.com', 'pond5', '', '_blank', 'pond5是一个综合素材网站，提供照片、插画、音乐、音效、AE模版和3D模式、视频等资源，该网站的音效素材也非常丰富，其素材也分为免费和付费内容，当然，这些内容都是有版权保证的。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(30, 'https://videohive.net', 'videohive', '', '_blank', 'videohive也是一个综合性较高的素材门户，当然除了音效，还有很多特效素材的提供，该门户分为国际版和国内版，而国际版的内容素材量优质，种类也比较丰富，所有素材都需要支付费用。但是该网站的素材确实质量较高，有非常多的声画效果值得参考。', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(31, 'http://ourspark.space/', '火花空间', '', '_blank', '几个北邮人做的创客推广平台，旨在培养大学生创新能力，促进中国大学创客教育', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(32, 'http://www.themepark.com.cn/', '主题公园', '', '_blank', 'wordpress的各种主题，其中有免费的，也有付费的，可以在上面尝试几个，可能会有复合自己审美口味的', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(33, 'http://stackoverflow.com/', 'stackoverflow', '', '_blank', '是一个与程序相关的IT技术问答网站。用户可以在网站免费提交问题，浏览问题，索引相关内容', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(34, 'https://kn007.net/code-highlight.html', '代码高亮化', '', '_blank', '只支持html、css、js、xml、php这几种语言的高亮化', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', ''),
(35, 'http://www.vrfavs.com/', 'VR网站仓库', '', '_blank', '汇集了国外涉及到VR的大部分知名网站，对VR感兴趣的闲暇时间可以逛逛', 'Y', 1, 0, '0000-00-00 00:00:00', '', '', '');
