(119, 1, '2017-03-15 16:00:47', '2017-03-15 08:00:47', '1.简单的图片预览\r\n\r\n&lt;pre lang=\"javascript\" line=\"1″&gt;\r\n\r\n//获取图片url地址\r\n\r\nfunction getObjectURL(file){\r\n\r\nvar url = null;\r\n\r\nif(window.createObjectURL != undefined){  //basic\r\n\r\nurl = window.createObjectURL(file);\r\n\r\n}else if(window.URL != undefined){  //mozila(firefox)\r\n\r\nurl = window.URL.createObjectURL(file);\r\n\r\n}else if(window.webkitURL != undefined){  //webkit or chrome\r\n\r\nurl = window.webkitURL.createObjectURL(file);\r\n\r\n}\r\n\r\nreturn url;\r\n\r\n};\r\n\r\n&lt;/pre&gt;', '一些实用的JS/jQuery方法', '', 'inherit', 'closed', 'closed', '', '114-revision-v1', '', '', '2017-03-15 16:00:47', '2017-03-15 08:00:47', '', 114, 'http://merrier.com/?p=119', 0, 'revision', '', 0),
(120, 1, '2017-03-15 16:01:21', '2017-03-15 08:01:21', '1.简单的图片预览\r\n\r\n&lt;pre lang=\"javascript\" line=\"1\"&gt;\r\n\r\n//获取图片url地址\r\n\r\nfunction getObjectURL(file){\r\n\r\nvar url = null;\r\n\r\nif(window.createObjectURL != undefined){  //basic\r\n\r\nurl = window.createObjectURL(file);\r\n\r\n}else if(window.URL != undefined){  //mozila(firefox)\r\n\r\nurl = window.URL.createObjectURL(file);\r\n\r\n}else if(window.webkitURL != undefined){  //webkit or chrome\r\n\r\nurl = window.webkitURL.createObjectURL(file);\r\n\r\n}\r\n\r\nreturn url;\r\n\r\n};\r\n\r\n&lt;/pre&gt;', '一些实用的JS/jQuery方法', '', 'inherit', 'closed', 'closed', '', '114-revision-v1', '', '', '2017-03-15 16:01:21', '2017-03-15 08:01:21', '', 114, 'http://merrier.com/?p=120', 0, 'revision', '', 0),
(121, 1, '2017-03-15 16:10:13', '2017-03-15 08:10:13', '<code>1.简单的图片预览</code>\r\n\r\n&lt;pre lang=\"javascript\" line=\"1\"&gt;\r\n\r\n//获取图片url地址\r\n\r\nfunction getObjectURL(file){\r\n\r\nvar url = null;\r\n\r\nif(window.createObjectURL != undefined){  //basic\r\n\r\nurl = window.createObjectURL(file);\r\n\r\n}else if(window.URL != undefined){  //mozila(firefox)\r\n\r\nurl = window.URL.createObjectURL(file);\r\n\r\n}else if(window.webkitURL != undefined){  //webkit or chrome\r\n\r\nurl = window.webkitURL.createObjectURL(file);\r\n\r\n}\r\n\r\nreturn url;\r\n\r\n};\r\n\r\n&lt;/pre&gt;', '一些实用的JS/jQuery方法', '', 'inherit', 'closed', 'closed', '', '114-revision-v1', '', '', '2017-03-15 16:10:13', '2017-03-15 08:10:13', '', 114, 'http://merrier.com/?p=121', 0, 'revision', '', 0),
