(128, 1, '2017-03-15 17:29:09', '2017-03-15 09:29:09', '<code>1.简单的图片预览</code>\r\n\r\n//获取图片url地址\r\n\r\nfunction getObjectURL(file){\r\n\r\nvar url = null;\r\n\r\nif(window.createObjectURL != undefined){  //basic\r\n\r\nurl = window.createObjectURL(file);\r\n\r\n}else if(window.URL != undefined){  //mozila(firefox)\r\n\r\nurl = window.URL.createObjectURL(file);\r\n\r\n}else if(window.webkitURL != undefined){  //webkit or chrome\r\n\r\nurl = window.webkitURL.createObjectURL(file);\r\n\r\n}\r\n\r\nreturn url;\r\n\r\n};\r\n\r\n&nbsp;', '一些实用的JS/jQuery方法', '', 'inherit', 'closed', 'closed', '', '114-revision-v1', '', '', '2017-03-15 17:29:09', '2017-03-15 09:29:09', '', 114, 'http://merrier.com/?p=128', 0, 'revision', '', 0),
(129, 1, '2017-03-15 17:29:29', '2017-03-15 09:29:29', '1.简单的图片预览\r\n\r\n//获取图片url地址\r\n\r\nfunction getObjectURL(file){\r\n\r\nvar url = null;\r\n\r\nif(window.createObjectURL != undefined){  //basic\r\n\r\nurl = window.createObjectURL(file);\r\n\r\n}else if(window.URL != undefined){  //mozila(firefox)\r\n\r\nurl = window.URL.createObjectURL(file);\r\n\r\n}else if(window.webkitURL != undefined){  //webkit or chrome\r\n\r\nurl = window.webkitURL.createObjectURL(file);\r\n\r\n}\r\n\r\nreturn url;\r\n\r\n};\r\n\r\n&nbsp;', '一些实用的JS/jQuery方法', '', 'inherit', 'closed', 'closed', '', '114-revision-v1', '', '', '2017-03-15 17:29:29', '2017-03-15 09:29:29', '', 114, 'http://merrier.com/?p=129', 0, 'revision', '', 0),
(130, 1, '2017-03-15 17:30:17', '2017-03-15 09:30:17', '1.简单的图片预览\r\n\r\n&lt;pre lang=\"javascript\" line=\"1\"&gt;\r\n\r\n//获取图片url地址\r\n\r\nfunction getObjectURL(file){\r\n\r\nvar url = null;\r\n\r\nif(window.createObjectURL != undefined){  //basic\r\n\r\nurl = window.createObjectURL(file);\r\n\r\n}else if(window.URL != undefined){  //mozila(firefox)\r\n\r\nurl = window.URL.createObjectURL(file);\r\n\r\n}else if(window.webkitURL != undefined){  //webkit or chrome\r\n\r\nurl = window.webkitURL.createObjectURL(file);\r\n\r\n}\r\n\r\nreturn url;\r\n\r\n};\r\n\r\n&lt;/pre&gt;', '一些实用的JS/jQuery方法', '', 'inherit', 'closed', 'closed', '', '114-revision-v1', '', '', '2017-03-15 17:30:17', '2017-03-15 09:30:17', '', 114, 'http://merrier.com/?p=130', 0, 'revision', '', 0);
