(180, 1, '2017-03-15 23:26:18', '2017-03-15 15:26:18', 'wordpress3wordpressbug\r\n<h1>1.wordpress</h1>\r\nwordpresswordpresswordpress-&gt;<a href=\"http://merrier.com/?p=35\">wordpress</a>\r\n<h1>2.WordPress</h1>\r\nfunctions.php= =\r\n<pre class=\"h-align:2 lang:js decode:true \">function autoset_featured() {\r\n  global $post;\r\n    $already_has_thumb = has_post_thumbnail($post-&gt;ID);\r\n      if (!$already_has_thumb)  {\r\n        $attached_image = get_children( \"post_parent=$post-&gt;ID&amp;post_type=attachment&amp;post_mime_type=image&amp;numberposts=1\" );\r\n          if ($attached_image) {\r\n            foreach ($attached_image as $attachment_id =&gt; $attachment) {\r\n              set_post_thumbnail($post-&gt;ID, $attachment_id);\r\n            }\r\n          }\r\n        }\r\n      }  //end function\r\nadd_action(\'the_post\', \'autoset_featured\');\r\nadd_action(\'save_post\', \'autoset_featured\');\r\nadd_action(\'draft_to_publish\', \'autoset_featured\');\r\nadd_action(\'new_to_publish\', \'autoset_featured\');\r\nadd_action(\'pending_to_publish\', \'autoset_featured\');\r\nadd_action(\'future_to_publish\', \'autoset_featured\');</pre>\r\n<h1>3.js</h1>\r\nWordPressfunctions.php \r\n<pre class=\"lang:js decode:true \" title=\"css\">/*\r\ncss.\r\n*/\r\n/*CSSmeta box*/\r\nadd_action(\'admin_menu\', \'cwp_add_my_custom_css_meta_box\');\r\n/*CSS*/\r\nadd_action(\'save_post\', \'cwp_save_my_custom_css\');\r\n/*CSS(Wordpress)*/\r\nadd_action(\'wp_head\',\'cwp_insert_my_custom_css\');\r\nfunction cwp_add_my_custom_css_meta_box() {\r\nadd_meta_box(\'my_custom_css\', \'CSS\', \'cwp_output_my_custom_css_input_fields\', \'post\', \'normal\', \'high\');\r\nadd_meta_box(\'my_custom_css\', \'CSS\', \'cwp_output_my_custom_css_input_fields\', \'page\', \'normal\', \'high\');\r\n}\r\nfunction cwp_output_my_custom_css_input_fields() {\r\nglobal $post;\r\necho \'&lt;input type=\"hidden\" name=\"my_custom_css_noncename\" id=\"my_custom_css_noncename\" value=\"\'.wp_create_nonce(\'custom-css\').\'\" /&gt;\';\r\necho \'&lt;textarea name=\"my_custom_css\" id=\"my_custom_css\" rows=\"5\" cols=\"30\" style=\"width:100%;\"&gt;\'.get_post_meta($post-&gt;ID,\'_my_custom_css\',true).\'&lt;/textarea&gt;\';\r\n}\r\nfunction cwp_save_my_custom_css($post_id) {\r\nif (!wp_verify_nonce($_POST[\'my_custom_css_noncename\'], \'custom-css\')) return $post_id;\r\nif (defined(\'DOING_AUTOSAVE\') &amp;&amp; DOING_AUTOSAVE) return $post_id;\r\n$my_custom_css = $_POST[\'my_custom_css\'];\r\nupdate_post_meta($post_id, \'_my_custom_css\', $my_custom_css);\r\n}\r\nfunction cwp_insert_my_custom_css() {\r\nif (is_page() || is_single()) {\r\nif (have_posts()) : while (have_posts()) : the_post();\r\necho \'&lt;style type=\"text/css\"&gt;\'.get_post_meta(get_the_ID(), \'_my_custom_css\', true).\'&lt;/style&gt;\';\r\nendwhile; endif;\r\nrewind_posts();\r\n}\r\n}</pre>\r\n\r\n\r\n<img class=\"alignnone wp-image-214\" src=\"http://merrier.com/wp-content/uploads/2017/03/QQ20170318-104451-300x174.png\" alt=\"\" width=\"548\" height=\"318\" />\r\n\r\nCSSjs\r\n\r\nWordPressfunctions.php \r\n<pre class=\"lang:js decode:true \" title=\"\">add_action(\"wp_head\",\"ruikeedu_custom_head\");\r\nfunction ruikeedu_custom_head(){\r\n    if (is_single() || is_page()) {\r\n        global $post;\r\n        $custom_head = get_post_meta($post-&gt;ID, \'custom_head\', true);\r\n        echo $custom_head;\r\n    }\r\n}</pre>\r\n\r\n\r\n<img class=\"alignnone wp-image-215\" src=\"http://merrier.com/wp-content/uploads/2017/03/QQ20170318-104857-300x89.png\" alt=\"\" width=\"644\" height=\"191\" />\r\n\r\ncustom_headcssjscss&lt;style&gt;&lt;/style&gt;js\r\n\r\ncssjs\r\n<h1>4. html </h1>\r\nWordPress  html WP html  functions.php \r\n<pre class=\"lang:js decode:true \" title=\"html\">/**\r\n* html\r\n* https://www.wpdaxue.com/category-description-support-html.html\r\n*/\r\nremove_filter(\'pre_term_description\', \'wp_filter_kses\');\r\nremove_filter(\'term_description\', \'wp_kses_data\');</pre>\r\n   html functions.php\r\n<pre class=\"lang:js decode:true \">// Disables Kses only for textarea saves\r\nforeach (array(\'pre_term_description\', \'pre_link_description\', \'pre_link_notes\', \'pre_user_description\') as $filter) {\r\nremove_filter($filter, \'wp_filter_kses\');\r\n}// Disables Kses only for textarea admin displays\r\nforeach (array(\'term_description\', \'link_description\', \'link_notes\', \'user_description\') as $filter) {\r\nremove_filter($filter, \'wp_kses_data\');\r\n}</pre>\r\n\r\n\r\n<a href=\"http://codex.wordpress.org/Function_Reference/wp_filter_kses\" target=\"_blank\">http://codex.wordpress.org/Function_Reference/wp_filter_kses</a>\r\n\r\n<a href=\"http://codex.wordpress.org/Function_Reference/wp_kses_data\" target=\"_blank\">http://codex.wordpress.org/Function_Reference/wp_kses_data</a>\r\n<h1>5.</h1>\r\nwordpressgifgif\r\n\r\n<img class=\"alignnone wp-image-250\" src=\"http://merrier.com/wp-content/uploads/2017/03/519ba533c895d143832ff8fb75f082025baf074c-300x229.png\" alt=\"\" width=\"562\" height=\"429\" />', 'wordpress', '', 'publish', 'open', 'open', '', 'wordpress%e5%bc%80%e5%8f%91%e8%a1%8c%e5%8a%a8%e6%8c%87%e5%8d%97', '', '', '2017-03-18 23:33:27', '2017-03-18 15:33:27', '', 0, 'http://merrier.com/?p=180', 0, 'post', '', 0),
