    left += 1\r\n        lists[right] = lists[left]\r\n    lists[right] = key\r\n    quick_sort(lists, low, left - 1)\r\n    quick_sort(lists, left + 1, high)\r\n    return lists</pre>\r\n5.\r\n\r\n1r1 ~ r[n]r12r2 ~ r[n]r2ir[i] ~ r[n]r[i]\r\n<pre class=\"lang:python decode:true \" title=\"\">def select_sort(lists):\r\n    # \r\n    count = len(lists)\r\n    for i in range(0, count):\r\n        min = i\r\n        for j in range(i + 1, count):\r\n            if lists[min] &gt; lists[j]:\r\n                min = j\r\n        lists[min], lists[i] = lists[i], lists[min]\r\n    return lists</pre>\r\n6.\r\n\r\n(Heapsort)A[PARENT[i]] &gt;= A[i]\r\n<pre class=\"lang:python decode:true \" title=\"\">def adjust_heap(lists, i, size):\r\n    lchild = 2 * i + 1\r\n    rchild = 2 * i + 2\r\n    max = i\r\n    if i &lt; size / 2:\r\n        if lchild &lt; size and lists[lchild] &gt; lists[max]:\r\n            max = lchild\r\n        if rchild &lt; size and lists[rchild] &gt; lists[max]:\r\n            max = rchild\r\n        if max != i:\r\n            lists[max], lists[i] = lists[i], lists[max]\r\n            adjust_heap(lists, max, size)\r\n\r\ndef build_heap(lists, size):\r\n    for i in range(0, (size/2))[::-1]:\r\n        adjust_heap(lists, i, size)\r\n\r\ndef heap_sort(lists):\r\n    size = len(lists)\r\n    build_heap(lists, size)\r\n    for i in range(0, size)[::-1]:\r\n        lists[0], lists[i] = lists[i], lists[0]\r\n        adjust_heap(lists, 0, i)</pre>\r\n7.\r\n\r\n,Divide and Conquer\r\n\r\na[i]a[j]a[i]a[j]a[i]r[k]ik1a[j]r[k]jk1rkt[s,t][s,t]\r\n<pre class=\"lang:python decode:true \" title=\"\">def merge(left, right):\r\n    i, j = 0, 0\r\n    result = []\r\n    while i &lt; len(left) and j &lt; len(right):\r\n        if left[i] &lt;= right[j]:\r\n            result.append(left[i])\r\n            i += 1\r\n        else:\r\n            result.append(right[j])\r\n            j += 1\r\n    result += left[i:]\r\n    result += right[j:]\r\n    return result\r\n\r\ndef merge_sort(lists):\r\n    # \r\n    if len(lists) &lt;= 1:\r\n        return lists\r\n    num = len(lists) / 2\r\n    left = merge_sort(lists[:num])\r\n    right = merge_sort(lists[num:])\r\n    return merge(left, right)</pre>\r\n8.\r\n\r\nradix sortdistribution sortbucket sortbin sortO (nlog(r)m)rm\r\n<pre class=\"lang:python decode:true \" title=\"\">import math\r\ndef radix_sort(lists, radix=10):\r\n    k = int(math.ceil(math.log(max(lists), radix)))\r\n    bucket = [[] for i in range(radix)]\r\n    for i in range(1, k+1):\r\n        for j in lists:\r\n            bucket[j/(radix**(i-1)) % (radix**i)].append(j)\r\n        del lists[:]\r\n        for z in bucket:\r\n            lists += z\r\n            del z[:]\r\n    return lists</pre>\r\n<!--more-->\r\n\r\n&nbsp;', 'python', '', 'inherit', 'closed', 'closed', '', '239-revision-v1', '', '', '2017-03-18 22:21:32', '2017-03-18 14:21:32', '', 239, 'http://merrier.com/archives/243', 0, 'revision', '', 0),
