 //\r\n                var tmp = arr[j]; arr[j]=arr[j+1];arr[j+1]=tmp;\r\n            }\r\n        i= pos; //\r\n    }\r\n    console.timeEnd(\'\');\r\n    return arr;\r\n}\r\nvar arr2=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(bubbleSort2(arr2));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\r\n\r\n/* \r\n arr-\r\n ,() , */\r\nfunction bubbleSort3(arr) {\r\n    var low = 0;\r\n    var high= arr.length-1; //\r\n    var tmp,j;\r\n    console.time(\'2.\');\r\n    while (low &lt; high) {\r\n        for (j= low; j&lt; high; ++j) //,\r\n            if (arr[j]&gt; arr[j+1]) {\r\n                tmp = arr[j]; arr[j]=arr[j+1];arr[j+1]=tmp;\r\n            }\r\n        --high;                 //high, \r\n        for (j=high; j&gt;low; --j) //,\r\n            if (arr[j]&lt;arr[j-1]) {\r\n                tmp = arr[j]; arr[j]=arr[j-1];arr[j-1]=tmp;\r\n            }\r\n        ++low;                  //low,\r\n    }\r\n    console.timeEnd(\'2.\');\r\n    return arr3;\r\n}\r\nvar arr3=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(bubbleSort3(arr3));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n)=&gt;</li>\r\n 	<li>T(n)=O(n2)=&gt;</li>\r\n 	<li>T(n)=O(n2)</li>\r\n</ul>\r\n<h1>2.</h1>\r\n<h2>1</h2>\r\n(Selection-sort)\r\n<h2>2</h2>\r\n<ol>\r\n 	<li>R[1..n]</li>\r\n 	<li>i(i=1,2,3n-1)R[1..i-1]R(i..n- R[k]1RR[1..i]R[i+1..n)11</li>\r\n 	<li>n-1</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-252 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/selection_sort.gif\" alt=\"\" width=\"811\" height=\"248\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n   arr-\r\n   */\r\nfunction selectionSort(arr) {\r\n    var len = arr.length;\r\n    var minIndex, temp;\r\n    console.time(\'\');\r\n    for (var i = 0; i &lt; len - 1; i++) {\r\n        minIndex = i;\r\n        for (var j = i + 1; j &lt; len; j++) {\r\n            if (arr[j] &lt; arr[minIndex]) {     //\r\n                minIndex = j;                 //\r\n            }\r\n        }\r\n        temp = arr[i];\r\n        arr[i] = arr[minIndex];\r\n        arr[minIndex] = temp;\r\n    }\r\n    console.timeEnd(\'\');\r\n    return arr;\r\n}\r\nvar arr=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(selectionSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n2)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n</ul>\r\n<h1>3.</h1>\r\n<h2>1</h2>\r\nInsertion-Sortin-placeO(1)\r\n<h2>2</h2>\r\n<ol>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li>3</li>\r\n 	<li></li>\r\n 	<li>2~5</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-253 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/insertion_sort.gif\" alt=\"\" width=\"811\" height=\"505\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n   array-\r\n   */\r\nfunction insertionSort(array) {\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\r\n        console.time(\'\');\r\n        for (var i = 1; i &lt; array.length; i++) {\r\n            var key = array[i];\r\n            var j = i - 1;\r\n            while (j &gt;= 0 &amp;&amp; array[j] &gt; key) {\r\n                array[j + 1] = array[j];\r\n                j--;\r\n            }\r\n            array[j + 1] = key;\r\n        }\r\n        console.timeEnd(\'\');\r\n        return array;\r\n    } else {\r\n        return \'array is not an Array!\';\r\n    }\r\n}\r\nvar arr1=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(insertionSort(arr1));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\r\n\r\n/* \r\n array-\r\n */\r\nfunction binaryInsertionSort(array) {\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\r\n        console.time(\'\');\r\n        for (var i = 1; i &lt; array.length; i++) {\r\n            var key = array[i], left = 0, right = i - 1;\r\n            while (left &lt;= right) {\r\n                var middle = parseInt((left + right) / 2);\r\n                if (key &lt; array[middle]) {\r\n                    right = middle - 1;\r\n                } else {\r\n                    left = middle + 1;\r\n                }\r\n            }\r\n            for (var j = i - 1; j &gt;= left; j--) {\r\n                array[j + 1] = array[j];\r\n            }\r\n            array[left] = key;\r\n        }\r\n        console.timeEnd(\'\');\r\n        return array;\r\n    } else {\r\n        return \'array is not an Array!\';\r\n    }\r\n}\r\nvar arr2=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(binaryInsertionSort(arr2));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n)=&gt;</li>\r\n 	<li>T(n)=O(n2)=&gt;</li>\r\n 	<li>T(n)=O(n2)</li>\r\n</ul>\r\n<h1>4.</h1>\r\n<h2>1</h2>\r\n4Robert Sedgewick\r\n<h2>2</h2>\r\n<ol>\r\n 	<li>t1t2tkti&gt;tjtk=1</li>\r\n 	<li>kk </li>\r\n 	<li>tim 1 </li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-254\" src=\"http://merrier.com/wp-content/uploads/2017/03/timg-2-300x232.jpeg\" alt=\"\" width=\"517\" height=\"400\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n   arr-\r\n   */\r\nfunction shellSort(arr) {\r\n    var len = arr.length,\r\n        temp,\r\n        gap = 1;\r\n    console.time(\':\');\r\n    while(gap &lt; len/5) {          //\r\n        gap =gap*5+1;\r\n    }\r\n    for (gap; gap &gt; 0; gap = Math.floor(gap/5)) {\r\n        for (var i = gap; i &lt; len; i++) {\r\n            temp = arr[i];\r\n            for (var j = i-gap; j &gt;= 0 &amp;&amp; arr[j] &gt; temp; j-=gap) {\r\n                arr[j+gap] = arr[j];\r\n            }\r\n            arr[j+gap] = temp;\r\n        }\r\n    }\r\n    console.timeEnd(\':\');\r\n    return arr;\r\n}\r\nvar arr=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(shellSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(nlog2n)</li>\r\n 	<li>T(n)=O(nlog2n)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n</ul>\r\n<h1>5.</h1>\r\n<h2>1</h2>\r\nDivide and Conquer2-\r\n<h2>2</h2>\r\n<ol>\r\n 	<li>nn/2</li>\r\n 	<li></li>\r\n 	<li></li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-255 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/merge_sort.gif\" alt=\"\" width=\"811\" height=\"505\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true\" title=\"\">/* \r\n   arr-\r\n   */\r\nfunction mergeSort(arr) {  //\r\n    var len = arr.length;\r\n    if(len &lt; 2) {\r\n        return arr;\r\n    }\r\n    var middle = Math.floor(len / 2),\r\n        left = arr.slice(0, middle),\r\n        right = arr.slice(middle);\r\n    return merge(mergeSort(left), mergeSort(right));\r\n}\r\nfunction merge(left, right)\r\n{\r\n    var result = [];\r\n    console.time(\'\');\r\n    while (left.length &amp;&amp; right.length) {\r\n        if (left[0] &lt;= right[0]) {\r\n            result.push(left.shift());\r\n        } else {\r\n            result.push(right.shift());\r\n        }\r\n    }\r\n    while (left.length)\r\n        result.push(left.shift());\r\n    while (right.length)\r\n        result.push(right.shift());\r\n    console.timeEnd(\'\');\r\n    return result;\r\n}\r\nvar arr=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(mergeSort(arr));</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n</ul>\r\n<h1>6.</h1>\r\n<h2>1</h2>\r\n\r\n<h2>2</h2>\r\n<ol>\r\n 	<li> pivot</li>\r\n 	<li>partition</li>\r\n 	<li>recursive</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-256 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/quick_sort.gif\" alt=\"\" width=\"811\" height=\"252\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n arr-\r\n */\r\nfunction quickSort(array, left, right) {\r\n    console.time(\'1.\');\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\' &amp;&amp; typeof left === \'number\' &amp;&amp; typeof right === \'number\') {\r\n        if (left &lt; right) {\r\n            var x = array[right],\r\n                i = left - 1,\r\n                temp;\r\n            for (var j = left; j &lt;= right; j++) {\r\n                if (array[j] &lt;= x) {\r\n                    i++;\r\n                    temp = array[i];\r\n                    array[i] = array[j];\r\n                    array[j] = temp;\r\n                }\r\n            }\r\n            quickSort(array, left, i - 1);\r\n            quickSort(array, i - 1, right);\r\n        }\r\n        console.timeEnd(\'1.\');\r\n        return array;\r\n    } else {\r\n        return \'array is not an Array or left or right is not a number!\';\r\n    }\r\n}\r\n\r\n/* \r\n arr-\r\n */\r\nfunction quickSort2(arr) {\r\n    console.time(\'2.\');\r\n    if (arr.length &lt;= 1) {\r\n        return arr;\r\n    }\r\n    var pivotIndex = Math.floor(arr.length / 2);\r\n    var pivot = arr.splice(pivotIndex, 1)[0];\r\n    var left = [];\r\n    var right = [];\r\n    for (var i = 0; i &lt; arr.length; i++) {\r\n        if (arr[i] &lt; pivot) {\r\n            left.push(arr[i]);\r\n        } else {\r\n            right.push(arr[i]);\r\n        }\r\n    }\r\n    console.timeEnd(\'2.\');\r\n    return quickSort2(left).concat([pivot], quickSort2(right));\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(quickSort(arr, 0, arr.length - 1));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\r\nconsole.log(quickSort2(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(nlogn)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n</ul>\r\n<h1>7.</h1>\r\n<h2>1</h2>\r\nHeapsort\r\n<h2>2</h2>\r\n<ol>\r\n 	<li>(R1,R2.Rn)</li>\r\n 	<li>R[1]R[n](R1,R2,Rn-1)(Rn),R[1,2n-1]&lt;=R[n]</li>\r\n 	<li>R[1](R1,R2,Rn-1)R[1](R1,R2.Rn-2)(Rn-1,Rn)n-1</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-257 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/heap_sort.gif\" alt=\"\" width=\"547\" height=\"364\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n array-\r\n */\r\nfunction heapSort(array) {\r\n    console.time(\'\');\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\r\n        //\r\n        var heapSize = array.length, temp;\r\n        for (var i = Math.floor(heapSize / 2) - 1; i &gt;= 0; i--) {\r\n            heaping(array, i, heapSize);\r\n        }\r\n        //\r\n        for (var j = heapSize - 1; j &gt;= 1; j--) {\r\n            temp = array[0];\r\n            array[0] = array[j];\r\n            array[j] = temp;\r\n            heaping(array, 0, --heapSize);\r\n        }\r\n        console.timeEnd(\'\');\r\n        return array;\r\n    } else {\r\n        return \'array is not an Array!\';\r\n    }\r\n}\r\n\r\n/* \r\n array-\r\n x-\r\n len-\r\n */\r\nfunction heaping(arr, x, len) {\r\n    if (Object.prototype.toString.call(arr).slice(8, -1) === \'Array\' &amp;&amp; typeof x === \'number\') {\r\n        var l = 2 * x + 1,\r\n            r = 2 * x + 2,\r\n            largest = x,\r\n            temp;\r\n        if (l &lt; len &amp;&amp; arr[l] &gt; arr[largest]) {\r\n            largest = l;\r\n        }\r\n        if (r &lt; len &amp;&amp; arr[r] &gt; arr[largest]) {\r\n            largest = r;\r\n        }\r\n        if (largest != x) {\r\n            temp = arr[x];\r\n            arr[x] = arr[largest];\r\n            arr[largest] = temp;\r\n            heaping(arr, largest, len);\r\n        }\r\n    } else {\r\n        return \'arr is not an Array or x is not a number!\';\r\n    }\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(heapSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(nlogn)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n</ul>\r\n<h1>8.</h1>\r\n<h2>1</h2>\r\n(Counting sort)CiAiCA\r\n<h2>2</h2>\r\n<ol>\r\n 	<li></li>\r\n 	<li>iCi</li>\r\n 	<li>C</li>\r\n 	<li>iC(i)C(i)1</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-258 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/counting_sort.gif\" alt=\"\" width=\"1012\" height=\"557\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n   array-\r\n   */\r\nfunction countingSort(array){\r\n    var len = array.length,\r\n        B = [],\r\n        C = [],\r\n        min = array[0],\r\n        max = array[0];\r\n    console.time(\'\');\r\n    for(var i =0;i&lt;len;i++){\r\n        min= min&lt;=array[i] ? min:array[i];\r\n        max = max &gt;=array[i] ? max : array[i];\r\n        C[array[i]] = C[array[i]] ? C[array[i]] +1:1;\r\n    }\r\n    for(var j = min; j&lt; max;j++){\r\n        C[j+1] = (C[j+1] || 0) + (C[j] || 0);\r\n    }\r\n    for(var k = len -1 ;k &gt;=0; k--){\r\n        B[C[array[k]] - 1] = array[k];\r\n        C[array[k]] --;\r\n    }\r\n    console.timeEnd(\'\');\r\n    return B;\r\n}\r\nvar arr=[2,2,3,8,7,1,2,2,2,7,3,9,8,2,1,4,2,4,6,9,2];\r\nconsole.log(countingSort(arr));//[1,1,2,2,2,2,2,2,2,2,3,3,4,4,6,7,7,8,8,9,9]</pre>\r\n<h2>5</h2>\r\nn0k O(n + k)C1\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n+k)</li>\r\n</ul>\r\n<h1>9.</h1>\r\n<h2>1</h2>\r\n (Bucket sort)\r\n<h2>2</h2>\r\n<ol>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li></li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-259\" src=\"http://merrier.com/wp-content/uploads/2017/03/timg-1-1-300x176.jpeg\" alt=\"\" width=\"465\" height=\"273\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n arr-\r\n num-\r\n */\r\nfunction bucketSort(array, num) {\r\n    if (array.length &lt;= 1) {\r\n        return array;\r\n    }\r\n    var len = array.length,\r\n        buckets = [],\r\n        result = [],\r\n        min = array[0],\r\n        max = array[0],\r\n        regex = \'/^[1-9]+[0-9]*$/\',\r\n        space,\r\n        n = 0;\r\n    num = num || ((num &gt; 1 &amp;&amp; regex.test(num)) ? num : 10);\r\n    console.time(\'\');\r\n    for (var i = 1; i &lt; len; i++) {\r\n        min = min &lt;= array[i] ? min : array[i];\r\n        max = max &gt;= array[i] ? max : array[i];\r\n    }\r\n    space = (max - min + 1) / num;\r\n    for (var j = 0; j &lt; len; j++) {\r\n        var index = Math.floor((array[j] - min) / space);\r\n        if (buckets[index]) { //\r\n            var k = buckets[index].length - 1;\r\n            while (k &gt;= 0 &amp;&amp; buckets[index][k] &gt; array[j]) {\r\n                buckets[index][k + 1] = buckets[index][k];\r\n                k--;\r\n            }\r\n            buckets[index][k + 1] = array[j];\r\n        } else {  //\r\n            buckets[index] = [];\r\n            buckets[index].push(array[j]);\r\n        }\r\n    }\r\n    while (n &lt; num) {\r\n        result = result.concat(buckets[n]);\r\n        n++;\r\n    }\r\n    console.timeEnd(\'\');\r\n    return result;\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(bucketSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\nO(n)O(n)\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n</ul>\r\n<h1>10.</h1>\r\n<h2>1</h2>\r\n\r\n<h2>2</h2>\r\n<ol>\r\n 	<li></li>\r\n 	<li>arrradix</li>\r\n 	<li>radix</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-260 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/radix_sort.gif\" alt=\"\" width=\"1012\" height=\"574\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n arr-\r\n maxDigit-\r\n \r\n 11000\r\n 20*/\r\nfunction radixSort(arr, maxDigit) {\r\n    var mod = 10,\r\n        dev = 1,\r\n        counter = [];\r\n    console.time(\'\');\r\n    for (var i = 0; i &lt; maxDigit; i++, dev *= 10, mod *= 10) {\r\n        for (var j = 0; j &lt; arr.length; j++) {\r\n            var bucket = parseInt((arr[j] % mod) / dev);\r\n            if (counter[bucket] == null) {\r\n                counter[bucket] = [];\r\n            }\r\n            counter[bucket].push(arr[j]);\r\n        }\r\n    }\r\n    var pos = 0;\r\n    for (var k = 0; k &lt; counter.length; k++) {\r\n        var value = null;\r\n        if (counter[k] != null) {\r\n            while ((value = counter[k].shift()) != null) {\r\n                arr[pos++] = value;\r\n            }\r\n        }\r\n    }\r\n    console.timeEnd(\'\');\r\n    return arr;\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(radixSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n*k)</li>\r\n 	<li>T(n)=O(n*k)</li>\r\n 	<li>T(n)=O(n*k)</li>\r\n</ul>\r\n&nbsp;', 'Javascript', '', 'publish', 'open', 'open', '', '%e5%8d%81%e5%a4%a7%e6%8e%92%e5%ba%8f%e7%ae%97%e6%b3%95%e7%9a%84javascript%e5%ae%9e%e7%8e%b0', '', '', '2017-03-18 23:20:09', '2017-03-18 15:20:09', '', 0, 'http://merrier.com/?p=244', 0, 'post', '', 0),
