(261, 1, '2017-03-18 23:13:55', '2017-03-18 15:13:55', '-JSJSCSS\r\n\r\nJSgithub<a href=\"https://github.com/damonare/Sorts\" target=\"_blank\"></a>\r\n\r\n\r\n\r\n1\r\n\r\n<strong></strong>aba=bab\r\n<strong></strong>aba=bab\r\n\r\n<strong></strong>\r\n<strong></strong>\r\n\r\n<strong></strong>: \r\n<strong></strong>: \r\n\r\n2\r\n\r\n<img class=\"alignnone  wp-image-245\" src=\"http://merrier.com/wp-content/uploads/2017/03/sort_complexity_summary-300x183.png\" alt=\"\" width=\"659\" height=\"402\" />\r\n\r\n\r\n\r\nn\r\n\r\nk\r\n\r\nIn-place\r\n\r\nOut-place\r\n\r\n3\r\n\r\n<img class=\"alignnone  wp-image-247\" src=\"http://merrier.com/wp-content/uploads/2017/03/QQ20170318-223548@2x-300x209.png\" alt=\"\" width=\"380\" height=\"265\" />\r\n\r\n1.bubble Sort\r\n\r\n1\r\n\r\n\r\n\r\n2\r\n<ol>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li>1~3</li>\r\n</ol>\r\n3\r\n\r\n<img class=\"alignnone wp-image-248 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/bubble_sort.gif\" alt=\"\" width=\"826\" height=\"257\" />\r\n\r\n4Javascript\r\n<pre class=\"lang:js decode:true\" title=\"\">/* \r\n   arr-\r\n   */\r\nfunction bubbleSort(arr) {\r\n    var len = arr.length;\r\n    for (var i = 0; i &lt; len; i++) {\r\n        for (var j = 0; j &lt; len - 1 - i; j++) {\r\n            if (arr[j] &gt; arr[j+1]) {        //\r\n                var temp = arr[j+1];        //\r\n                arr[j+1] = arr[j];\r\n                arr[j] = temp;\r\n            }\r\n        }\r\n    }\r\n    return arr;\r\n}\r\nvar arr1=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(bubbleSort(arr1));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\r\n\r\n/* \r\n arr-\r\n pos,pos,pos*/\r\nfunction bubbleSort2(arr) {\r\n    console.time(\'\');\r\n    var i = arr.length-1;  //,\r\n    while ( i&gt; 0) {\r\n        var pos= 0; //,\r\n        for (var j= 0; j&lt; i; j++)\r\n            if (arr[j]&gt; arr[j+1]) {\r\n                pos= j; //\r\n                var tmp = arr[j]; arr[j]=arr[j+1];arr[j+1]=tmp;\r\n            }\r\n        i= pos; //\r\n    }\r\n    console.timeEnd(\'\');\r\n    return arr;\r\n}\r\nvar arr2=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(bubbleSort2(arr2));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\r\n\r\n/* \r\n arr-\r\n ,() , */\r\nfunction bubbleSort3(arr) {\r\n    var low = 0;\r\n    var high= arr.length-1; //\r\n    var tmp,j;\r\n    console.time(\'2.\');\r\n    while (low &lt; high) {\r\n        for (j= low; j&lt; high; ++j) //,\r\n            if (arr[j]&gt; arr[j+1]) {\r\n                tmp = arr[j]; arr[j]=arr[j+1];arr[j+1]=tmp;\r\n            }\r\n        --high;                 //high, \r\n        for (j=high; j&gt;low; --j) //,\r\n            if (arr[j]&lt;arr[j-1]) {\r\n                tmp = arr[j]; arr[j]=arr[j-1];arr[j-1]=tmp;\r\n            }\r\n        ++low;                  //low,\r\n    }\r\n    console.timeEnd(\'2.\');\r\n    return arr3;\r\n}\r\nvar arr3=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(bubbleSort3(arr3));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n5\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n)=&gt;</li>\r\n 	<li>T(n)=O(n2)=&gt;</li>\r\n 	<li>T(n)=O(n2)</li>\r\n</ul>\r\n2.\r\n\r\n1\r\n\r\n(Selection-sort)\r\n\r\n2\r\n<ol>\r\n 	<li>R[1..n]</li>\r\n 	<li>i(i=1,2,3n-1)R[1..i-1]R(i..n- R[k]1RR[1..i]R[i+1..n)11</li>\r\n 	<li>n-1</li>\r\n</ol>\r\n3\r\n\r\n<img class=\"alignnone wp-image-252 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/selection_sort.gif\" alt=\"\" width=\"811\" height=\"248\" />\r\n\r\n4Javascript\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n   arr-\r\n   */\r\nfunction selectionSort(arr) {\r\n    var len = arr.length;\r\n    var minIndex, temp;\r\n    console.time(\'\');\r\n    for (var i = 0; i &lt; len - 1; i++) {\r\n        minIndex = i;\r\n        for (var j = i + 1; j &lt; len; j++) {\r\n            if (arr[j] &lt; arr[minIndex]) {     //\r\n                minIndex = j;                 //\r\n            }\r\n        }\r\n        temp = arr[i];\r\n        arr[i] = arr[minIndex];\r\n        arr[minIndex] = temp;\r\n    }\r\n    console.timeEnd(\'\');\r\n    return arr;\r\n}\r\nvar arr=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(selectionSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n5\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n2)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n</ul>\r\n3.\r\n\r\n1\r\n\r\nInsertion-Sortin-placeO(1)\r\n\r\n2\r\n<ol>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li>3</li>\r\n 	<li></li>\r\n 	<li>2~5</li>\r\n</ol>\r\n3\r\n\r\n<img class=\"alignnone wp-image-253 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/insertion_sort.gif\" alt=\"\" width=\"811\" height=\"505\" />\r\n\r\n4Javascript\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n   array-\r\n   */\r\nfunction insertionSort(array) {\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\r\n        console.time(\'\');\r\n        for (var i = 1; i &lt; array.length; i++) {\r\n            var key = array[i];\r\n            var j = i - 1;\r\n            while (j &gt;= 0 &amp;&amp; array[j] &gt; key) {\r\n                array[j + 1] = array[j];\r\n                j--;\r\n            }\r\n            array[j + 1] = key;\r\n        }\r\n        console.timeEnd(\'\');\r\n        return array;\r\n    } else {\r\n        return \'array is not an Array!\';\r\n    }\r\n}\r\nvar arr1=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\r\nconsole.log(insertionSort(arr1));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\r\n\r\n/* \r\n array-\r\n */\r\nfunction binaryInsertionSort(array) {\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\r\n        console.time(\'\');\r\n        for (var i = 1; i &lt; array.length; i++) {\r\n            var key = array[i], left = 0, right = i - 1;\r\n            while 