r;\n}\nvar arr=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\nconsole.log(selectionSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\n<h2>5</h2>\n<ul class=\"ul_circle\">\n 	<li>T(n)=O(n2)</li>\n 	<li>T(n)=O(n2)</li>\n 	<li>T(n)=O(n2)</li>\n</ul>\n<h1>3.</h1>\n<h2>1</h2>\nInsertion-Sortin-placeO(1)\n<h2>2</h2>\n<ol>\n 	<li></li>\n 	<li></li>\n 	<li></li>\n 	<li>3</li>\n 	<li></li>\n 	<li>2~5</li>\n</ol>\n<h2>3</h2>\n<img class=\"alignnone wp-image-253 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/insertion_sort.gif\" alt=\"\" width=\"811\" height=\"505\" />\n<h2>4Javascript</h2>\n<pre class=\"lang:js decode:true \" title=\"\">/* \n   array-\n   */\nfunction insertionSort(array) {\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\n        console.time(\'\');\n        for (var i = 1; i &lt; array.length; i++) {\n            var key = array[i];\n            var j = i - 1;\n            while (j &gt;= 0 &amp;&amp; array[j] &gt; key) {\n                array[j + 1] = array[j];\n                j--;\n            }\n            array[j + 1] = key;\n        }\n        console.timeEnd(\'\');\n        return array;\n    } else {\n        return \'array is not an Array!\';\n    }\n}\nvar arr1=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\nconsole.log(insertionSort(arr1));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\n\n/* \n array-\n */\nfunction binaryInsertionSort(array) {\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\n        console.time(\'\');\n        for (var i = 1; i &lt; array.length; i++) {\n            var key = array[i], left = 0, right = i - 1;\n            while (left &lt;= right) {\n                var middle = parseInt((left + right) / 2);\n                if (key &lt; array[middle]) {\n                    right = middle - 1;\n                } else {\n                    left = middle + 1;\n                }\n            }\n            for (var j = i - 1; j &gt;= left; j--) {\n                array[j + 1] = array[j];\n            }\n            array[left] = key;\n        }\n        console.timeEnd(\'\');\n        return array;\n    } else {\n        return \'array is not an Array!\';\n    }\n}\nvar arr2=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\nconsole.log(binaryInsertionSort(arr2));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\n<h2>5</h2>\n<ul class=\"ul_circle\">\n 	<li>T(n)=O(n)=&gt;</li>\n 	<li>T(n)=O(n2)=&gt;</li>\n 	<li>T(n)=O(n2)</li>\n</ul>\n<h1>4.</h1>\n<h2>1</h2>\n4Robert Sedgewick\n<h2>2</h2>\n<ol>\n 	<li>t1t2tkti&gt;tjtk=1</li>\n 	<li>kk </li>\n 	<li>tim 1 </li>\n</ol>\n<h2>3</h2>\n<img class=\"alignnone wp-image-254\" src=\"http://merrier.com/wp-content/uploads/2017/03/timg-2-300x232.jpeg\" alt=\"\" width=\"517\" height=\"400\" />\n<h2>4Javascript</h2>\n<pre class=\"lang:js decode:true \" title=\"\">/* \n   arr-\n   */\nfunction shellSort(arr) {\n    var len = arr.length,\n        temp,\n        gap = 1;\n    console.time(\':\');\n    while(gap &lt; len/5) {          //\n        gap =gap*5+1;\n    }\n    for (gap; gap &gt; 0; gap = Math.floor(gap/5)) {\n        for (var i = gap; i &lt; len; i++) {\n            temp = arr[i];\n            for (var j = i-gap; j &gt;= 0 &amp;&amp; arr[j] &gt; temp; j-=gap) {\n                arr[j+gap] = arr[j];\n            }\n            arr[j+gap] = temp;\n        }\n    }\n    console.timeEnd(\':\');\n    return arr;\n}\nvar arr=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\nconsole.log(shellSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\n<h2>5</h2>\n<ul class=\"ul_circle\">\n 	<li>T(n)=O(nlog2n)</li>\n 	<li>T(n)=O(nlog2n)</li>\n 	<li>T(n)=O(nlogn)</li>\n</ul>\n<h1>5.</h1>\n<h2>1</h2>\nDivide and Conquer2-\n<h2>2</h2>\n<ol>\n 	<li>nn/2</li>\n 	<li></li>\n 	<li></li>\n</ol>\n<h2>3</h2>\n<img class=\"alignnone wp-image-255 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/merge_sort.gif\" alt=\"\" width=\"811\" height=\"505\" />\n<h2>4Javascript</h2>\n<pre class=\"lang:js decode:true\" title=\"\">/* \n   arr-\n   */\nfunction mergeSort(arr) {  //\n    var len = arr.length;\n    if(len &lt; 2) {\n        return arr;\n    }\n    var middle = Math.floor(len / 2),\n        left = arr.slice(0, middle),\n        right = arr.slice(middle);\n    return merge(mergeSort(left), mergeSort(right));\n}\nfunction merge(left, right)\n{\n    var result = [];\n    console.time(\'\');\n    while (left.length &amp;&amp; right.length) {\n        if (left[0] &lt;= right[0]) {\n            result.push(left.shift());\n        } else {\n            result.push(right.shift());\n        }\n    }\n    while (left.length)\n        result.push(left.shift());\n    while (right.length)\n        result.push(right.shift());\n    console.timeEnd(\'\');\n    return result;\n}\nvar arr=[3,44,38,5,47,15,36,26,27,2,46,4,19,50,48];\nconsole.log(mergeSort(arr));</pre>\n<h2>5</h2>\n<ul class=\"ul_circle\">\n 	<li>T(n)=O(n)</li>\n 	<li>T(n)=O(nlogn)</li>\n 	<li>T(n)=O(nlogn)</li>\n</ul>\n<h1>6.</h1>\n<h2>1</h2>\n\n<h2>2</h2>\n<ol>\n 	<li> pivot</li>\n 	<li>partition</li>\n 	<li>recursive</li>\n</ol>\n<h2>3</h2>\n<img class=\"alignnone wp-image-256 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/quick_sort.gif\" alt=\"\" width=\"811\" height=\"252\" />\n<h2>4Javascript</h2>\n<pre class=\"lang:js decode:true \" title=\"\">/* \n arr-\n */\nfunction quickSort(array, left, right) {\n    console.time(\'1.\');\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\' &amp;&amp; typeof left === \'number\' &amp;&amp; typeof right === \'number\') {\n        if (left &lt; right) {\n            var x = array[right],\n                i = left - 1,\n                temp;\n            for (var j = left; j &lt;= right; j++) {\n                if (array[j] &lt;= x) {\n                    i++;\n                    temp = array[i];\n                    array[i] = array[j];\n                    array[j] = temp;\n                }\n            }\n            quickSort(array, left, i - 1);\n            quickSort(array, i - 1, right);\n        }\n        console.timeEnd(\'1.\');\n        return array;\n    } else {\n        return \'array is not an Array or left or right is not a number!\';\n    }\n}\n\n/* \n arr-\n */\nfunction quickSort2(arr) {\n    console.time(\'2.\');\n    if (arr.length &lt;= 1) {\n        return arr;\n    }\n    var pivotIndex = Math.floor(arr.length / 2);\n    var pivot = arr.splice(pivotIndex, 1)[0];\n    var left = [];\n    var right = [];\n    for (var i = 0; i &lt; arr.length; i++) {\n        if (arr[i] &lt; pivot) {\n            left.push(arr[i]);\n        } else {\n            right.push(arr[i]);\n        }\n    }\n    console.timeEnd(\'2.\');\n    return quickSort2(left).concat([pivot], quickSort2(right));\n}\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\nconsole.log(quickSort(arr, 0, arr.length - 1));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\nconsole.log(quickSort2(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\n<h2>5</h2>\n<ul class=\"ul_circle\">\n 	<li>T(n)=O(nlogn)</li>\n 	<li>T(n)=O(n2)</li>\n 	<li>T(n)=O(nlogn)</li>\n</ul>\n<h1>7.</h1>\n<h2>1</h2>\nHeapsort\n<h2>2</h2>\n<ol>\n 	<li>(R1,R2.Rn)</li>\n 	<li>R[1]R[n](R1,R2,Rn-1)(Rn),R[1,2n-1]&lt;=R[n]</li>\n 	<li>R[1](R1,R2,Rn-1)R[1](R1,R2.Rn-2)(Rn-1,Rn)n-1</li>\n</ol>\n<h2>3</h2>\n<img class=\"alignnone wp-image-257 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/heap_sort.gif\" alt=\"\" width=\"547\" height=\"364\" />\n<h2>4Javascript</h2>\n<pre class=\"lang:js decode:true \" title=\"\">/* \n array-\n */\nfunction heapSort(array) {\n    console.time(\'\');\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\n        //\n        var heapSize = array.length, temp;\n        for (var i = Math.floor(heapSize / 2) - 1; i &gt;= 0; i--) {\n            heaping(array, i, heapSize);\n        }\n        //\n        for (var j = heapSize - 1; j &gt;= 1; j--) {\n            temp = array[0];\n            array[0] = array[j];\n            array[j] = temp;\n            heaping(array, 0, --heapSize);\n        }\n        console.timeEnd(\'\');\n        return array;\n    } else {\n        return \'array is not an Array!\';\n    }\n}\n\n/* \n array-\n x-\n len-\n */\nfunction heaping(arr, x, len) {\n    if (Object.prototype.toString.call(arr).slice(8, -1) === \'Array\' &amp;&amp; typeof x === \'number\') {\n        var l = 2 * x + 1,\n            r = 2 * x + 2,\n            largest = x,\n            temp;\n        if (l &lt; len &amp;&amp; arr[l] &gt; arr[largest]) {\n            largest = l;\n        }\n        if (r &lt; len &amp;&amp; arr[r] &gt; arr[largest]) {\n            largest = r;\n        }\n        if (largest != x) {\n            temp = arr[x];\n            arr[x] = arr[largest];\n            arr[largest] = temp;\n            heaping(arr, largest, len);\n        }\n    } else {\n        return \'arr is not an Array or x is not a number!\';\n    }\n}\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\nconsole.log(heapSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\n<h2>5</h2>\n<ul class=\"ul_circle\">\n 	<li>T(n)=O(nlogn)</li>\n 	<li>T(n)=O(nlogn)</li>\n 	<li>T(n)=O(nlogn)</li>\n</ul>\n<h1>8.</h1>\n<h2>1</h2>\n(Counting sort)CiAiCA\n<h2>2</h2>\n<ol>\n 	<li></li>\n 	<li>iCi</li>\n 	<li>C</li>\n 	<li>iC(i)C(i)1</li>\n</ol>\n<h2>3</h2>\n<img class=\"alignnone wp-image-258 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/counting_sort.gif\" alt=\"\" width=\"1012\" height=\"557\" />\n\n4Javascript\n<pre class=\"lang:js decode:true \" title=\"\">/* \n   array-\n   */\nfunction countingSort(array){\n    var len = array.length,\n        B = [],\n        C = [],\n        min = array[0],\n        max = array[0];\n    console.time(\'\');\n    for(var i =0;i&lt;len;i++){\n        min= min&lt