/>\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n arr-\r\n */\r\nfunction quickSort(array, left, right) {\r\n    console.time(\'1.\');\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\' &amp;&amp; typeof left === \'number\' &amp;&amp; typeof right === \'number\') {\r\n        if (left &lt; right) {\r\n            var x = array[right],\r\n                i = left - 1,\r\n                temp;\r\n            for (var j = left; j &lt;= right; j++) {\r\n                if (array[j] &lt;= x) {\r\n                    i++;\r\n                    temp = array[i];\r\n                    array[i] = array[j];\r\n                    array[j] = temp;\r\n                }\r\n            }\r\n            quickSort(array, left, i - 1);\r\n            quickSort(array, i - 1, right);\r\n        }\r\n        console.timeEnd(\'1.\');\r\n        return array;\r\n    } else {\r\n        return \'array is not an Array or left or right is not a number!\';\r\n    }\r\n}\r\n\r\n/* \r\n arr-\r\n */\r\nfunction quickSort2(arr) {\r\n    console.time(\'2.\');\r\n    if (arr.length &lt;= 1) {\r\n        return arr;\r\n    }\r\n    var pivotIndex = Math.floor(arr.length / 2);\r\n    var pivot = arr.splice(pivotIndex, 1)[0];\r\n    var left = [];\r\n    var right = [];\r\n    for (var i = 0; i &lt; arr.length; i++) {\r\n        if (arr[i] &lt; pivot) {\r\n            left.push(arr[i]);\r\n        } else {\r\n            right.push(arr[i]);\r\n        }\r\n    }\r\n    console.timeEnd(\'2.\');\r\n    return quickSort2(left).concat([pivot], quickSort2(right));\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(quickSort(arr, 0, arr.length - 1));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]\r\nconsole.log(quickSort2(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(nlogn)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n</ul>\r\n<h1>7.</h1>\r\n<h2>1</h2>\r\nHeapsort\r\n<h2>2</h2>\r\n<ol>\r\n 	<li>(R1,R2.Rn)</li>\r\n 	<li>R[1]R[n](R1,R2,Rn-1)(Rn),R[1,2n-1]&lt;=R[n]</li>\r\n 	<li>R[1](R1,R2,Rn-1)R[1](R1,R2.Rn-2)(Rn-1,Rn)n-1</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-257 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/heap_sort.gif\" alt=\"\" width=\"547\" height=\"364\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n array-\r\n */\r\nfunction heapSort(array) {\r\n    console.time(\'\');\r\n    if (Object.prototype.toString.call(array).slice(8, -1) === \'Array\') {\r\n        //\r\n        var heapSize = array.length, temp;\r\n        for (var i = Math.floor(heapSize / 2) - 1; i &gt;= 0; i--) {\r\n            heaping(array, i, heapSize);\r\n        }\r\n        //\r\n        for (var j = heapSize - 1; j &gt;= 1; j--) {\r\n            temp = array[0];\r\n            array[0] = array[j];\r\n            array[j] = temp;\r\n            heaping(array, 0, --heapSize);\r\n        }\r\n        console.timeEnd(\'\');\r\n        return array;\r\n    } else {\r\n        return \'array is not an Array!\';\r\n    }\r\n}\r\n\r\n/* \r\n array-\r\n x-\r\n len-\r\n */\r\nfunction heaping(arr, x, len) {\r\n    if (Object.prototype.toString.call(arr).slice(8, -1) === \'Array\' &amp;&amp; typeof x === \'number\') {\r\n        var l = 2 * x + 1,\r\n            r = 2 * x + 2,\r\n            largest = x,\r\n            temp;\r\n        if (l &lt; len &amp;&amp; arr[l] &gt; arr[largest]) {\r\n            largest = l;\r\n        }\r\n        if (r &lt; len &amp;&amp; arr[r] &gt; arr[largest]) {\r\n            largest = r;\r\n        }\r\n        if (largest != x) {\r\n            temp = arr[x];\r\n            arr[x] = arr[largest];\r\n            arr[largest] = temp;\r\n            heaping(arr, largest, len);\r\n        }\r\n    } else {\r\n        return \'arr is not an Array or x is not a number!\';\r\n    }\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(heapSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(nlogn)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n 	<li>T(n)=O(nlogn)</li>\r\n</ul>\r\n<h1>8.</h1>\r\n<h2>1</h2>\r\n(Counting sort)CiAiCA\r\n<h2>2</h2>\r\n<ol>\r\n 	<li></li>\r\n 	<li>iCi</li>\r\n 	<li>C</li>\r\n 	<li>iC(i)C(i)1</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-258 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/counting_sort.gif\" alt=\"\" width=\"1012\" height=\"557\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n   array-\r\n   */\r\nfunction countingSort(array){\r\n    var len = array.length,\r\n        B = [],\r\n        C = [],\r\n        min = array[0],\r\n        max = array[0];\r\n    console.time(\'\');\r\n    for(var i =0;i&lt;len;i++){\r\n        min= min&lt;=array[i] ? min:array[i];\r\n        max = max &gt;=array[i] ? max : array[i];\r\n        C[array[i]] = C[array[i]] ? C[array[i]] +1:1;\r\n    }\r\n    for(var j = min; j&lt; max;j++){\r\n        C[j+1] = (C[j+1] || 0) + (C[j] || 0);\r\n    }\r\n    for(var k = len -1 ;k &gt;=0; k--){\r\n        B[C[array[k]] - 1] = array[k];\r\n        C[array[k]] --;\r\n    }\r\n    console.timeEnd(\'\');\r\n    return B;\r\n}\r\nvar arr=[2,2,3,8,7,1,2,2,2,7,3,9,8,2,1,4,2,4,6,9,2];\r\nconsole.log(countingSort(arr));//[1,1,2,2,2,2,2,2,2,2,3,3,4,4,6,7,7,8,8,9,9]</pre>\r\n<h2>5</h2>\r\nn0k O(n + k)C1\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n+k)</li>\r\n</ul>\r\n<h1>9.</h1>\r\n<h2>1</h2>\r\n (Bucket sort)\r\n<h2>2</h2>\r\n<ol>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li></li>\r\n 	<li></li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-259\" src=\"http://merrier.com/wp-content/uploads/2017/03/timg-1-1-300x176.jpeg\" alt=\"\" width=\"465\" height=\"273\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n arr-\r\n num-\r\n */\r\nfunction bucketSort(array, num) {\r\n    if (array.length &lt;= 1) {\r\n        return array;\r\n    }\r\n    var len = array.length,\r\n        buckets = [],\r\n        result = [],\r\n        min = array[0],\r\n        max = array[0],\r\n        regex = \'/^[1-9]+[0-9]*$/\',\r\n        space,\r\n        n = 0;\r\n    num = num || ((num &gt; 1 &amp;&amp; regex.test(num)) ? num : 10);\r\n    console.time(\'\');\r\n    for (var i = 1; i &lt; len; i++) {\r\n        min = min &lt;= array[i] ? min : array[i];\r\n        max = max &gt;= array[i] ? max : array[i];\r\n    }\r\n    space = (max - min + 1) / num;\r\n    for (var j = 0; j &lt; len; j++) {\r\n        var index = Math.floor((array[j] - min) / space);\r\n        if (buckets[index]) { //\r\n            var k = buckets[index].length - 1;\r\n            while (k &gt;= 0 &amp;&amp; buckets[index][k] &gt; array[j]) {\r\n                buckets[index][k + 1] = buckets[index][k];\r\n                k--;\r\n            }\r\n            buckets[index][k + 1] = array[j];\r\n        } else {  //\r\n            buckets[index] = [];\r\n            buckets[index].push(array[j]);\r\n        }\r\n    }\r\n    while (n &lt; num) {\r\n        result = result.concat(buckets[n]);\r\n        n++;\r\n    }\r\n    console.timeEnd(\'\');\r\n    return result;\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(bucketSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\nO(n)O(n)\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n+k)</li>\r\n 	<li>T(n)=O(n2)</li>\r\n</ul>\r\n<h1>10.</h1>\r\n<h2>1</h2>\r\n\r\n<h2>2</h2>\r\n<ol>\r\n 	<li></li>\r\n 	<li>arrradix</li>\r\n 	<li>radix</li>\r\n</ol>\r\n<h2>3</h2>\r\n<img class=\"alignnone wp-image-260 size-full\" src=\"http://merrier.com/wp-content/uploads/2017/03/radix_sort.gif\" alt=\"\" width=\"1012\" height=\"574\" />\r\n<h2>4Javascript</h2>\r\n<pre class=\"lang:js decode:true \" title=\"\">/* \r\n arr-\r\n maxDigit-\r\n \r\n 11000\r\n 20*/\r\nfunction radixSort(arr, maxDigit) {\r\n    var mod = 10,\r\n        dev = 1,\r\n        counter = [];\r\n    console.time(\'\');\r\n    for (var i = 0; i &lt; maxDigit; i++, dev *= 10, mod *= 10) {\r\n        for (var j = 0; j &lt; arr.length; j++) {\r\n            var bucket = parseInt((arr[j] % mod) / dev);\r\n            if (counter[bucket] == null) {\r\n                counter[bucket] = [];\r\n            }\r\n            counter[bucket].push(arr[j]);\r\n        }\r\n    }\r\n    var pos = 0;\r\n    for (var k = 0; k &lt; counter.length; k++) {\r\n        var value = null;\r\n        if (counter[k] != null) {\r\n            while ((value = counter[k].shift()) != null) {\r\n                arr[pos++] = value;\r\n            }\r\n        }\r\n    }\r\n    console.timeEnd(\'\');\r\n    return arr;\r\n}\r\nvar arr = [3, 44, 38, 5, 47, 15, 36, 26, 27, 2, 46, 4, 19, 50, 48];\r\nconsole.log(radixSort(arr));//[2, 3, 4, 5, 15, 19, 26, 27, 36, 38, 44, 46, 47, 48, 50]</pre>\r\n<h2>5</h2>\r\n<ul class=\"ul_circle\">\r\n 	<li>T(n)=O(n*k)</li>\r\n 	<li>T(n)=O(n*k)</li>\r\n 	<li>T(n)=O(n*k)</li>\r\n</ul>\r\n&nbsp;', 'Javascript', '', 'inherit', 'closed', 'closed', '', '244-revision-v1', '', '', '2017-03-18 23:20:09', '2017-03-18 15:20:09', '', 244, 'http://merrier.com/archives/264', 0, 'revision', '', 0);
